public class Codec{	private	int[] mapTo;	private int[] mapFrom;	public Codec(long myKey, int size)	{		mapTo = new int[size];		mapFrom = new int [size];		int hiPointer = size - 1;		int loPointer = 0;		int bitmask = 1 << 16;		for (int i = 0; i < mapTo.length; ++i)		{			if ((bitmask & myKey) != 0)			{				mapTo[i] = hiPointer;				mapFrom[hiPointer] = i;				--hiPointer;			}			else			{				mapTo[i] = loPointer;				mapFrom[loPointer] = i;				++loPointer;			}			bitmask >>>= 1;			if (bitmask == 0)				bitmask = 1 << 16;		}	}	public int encode(int ix)	{		return mapTo[ix];	}	public int decode(int ix)	{		return mapFrom[ix];	}}