/** *	The Macroblock class, represents 8*8-pixel block of*	light intensity information.*/public class Macroblock extends java.util.ArrayList{    public Macroblock()    {        super();    }	/** two equivalent methods that support the	*	shifting of information within a macroblock	*/	public void shiftInBits(int newBits, int numBits)	{		int mask = 1 << (numBits - 1);		while (numBits > 0)		{			add(new Boolean((newBits & mask) != 0));			mask >>>= 1;			--numBits;		}	}    public void shiftInBits(long newBits, int numBits)    {        shiftInBits((int) newBits, numBits);    }}